import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateChargerById = () => {
  const [id, setId] = useState(0);
  const [charger, setCharger] = useState(null);
  const [longitude, setLongitude] = useState('');
  const [latitude, setLatitude] = useState('');
  const [rate, setRate] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      if (id) {
        try {
          const response = await axios.get(`http://localhost:8080/api/chargers/getcharger/${id}`); // Replace with your exact API endpoint URL
          setCharger(response.data);
          setLongitude(response.data.longitude); // Set initial values from fetched data
          setLatitude(response.data.latitude);
          setRate(response.data.rate);
        } catch (error) {
          console.error(error);
          setCharger(null);
        }
      }
    };

    fetchData();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedCharger = {
      id,
      longitude,
      latitude,
      rate,
    };

    try {
      await axios.put(`http://localhost:8080/api/chargers/update/${id}`, updatedCharger); // Replace with your API endpoint URL
      alert('Charger updated successfully!');
      setCharger(updatedCharger); // Update local state with updated data
    } catch (error) {
      console.error(error);
      alert('Error updating charger!');
    }
  };

  return (
    <div>
      <h1>Update Charger by ID</h1>
      {charger ? (
        <form onSubmit={handleSubmit}>
          <label style={{ color: 'white' }}> {/* Style the entire label */}
            ID:
            <input type="number" value={id} disabled /> {/* Display ID but disable editing */}
          </label>
          <label style={{ color: 'white' }}> {/* Style the entire label */}
            Longitude:
            <input type="text" value={longitude} onChange={(e) => setLongitude(e.target.value)} />
          </label>
          <label style={{ color: 'white' }}> {/* Style the entire label */}
            Latitude:
            <input type="text" value={latitude} onChange={(e) => setLatitude(e.target.value)} />
          </label>
          <label style={{ color: 'white' }}> {/* Style the entire label */}
            Rate:
            <input type="number" value={rate} onChange={(e) => setRate(e.target.value)} />
          </label>
          <button className="home-hero-button1 buttonFilled">update</button>
        </form>
      ) : (
        <p>Enter a charger ID to fetch details for update.</p>
      )}
    </div>
  );
};

export default UpdateChargerById;
